        # Read until video is completed
        while(cap.isOpened()):
          # Capture frame-by-frame
          ret, frame = cap.read()
          if ret == True:
            count += 1

            if fps % 2 == 0: # fps pari
              if (count % fps == 0 or (count*2) % fps == 0): # get 2 frames per second
                list_of_frames.append(frame)
              if count % fps == 0:
                count = 0
            else: # fps dispari
              if (count % fps == 0 or (count*2) % (fps+1) == 0): # get 2 frames per second
                list_of_frames.append(frame)
